import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  constructor() { }

  ngOnInit(): void { }
}
<div>
  <h1 style="text-align: center">Para el uso de banners:</h1>
  <app-banner></app-banner>
  <hr />
</div>

<p>Para el uso de portadas:</p>
<app-front-page></app-front-page>
<p>Para el menu de navegación:</p>
<app-navbar></app-navbar>
<p>Para el login:</p>
<app-login></app-login>
<p>Para las noticias:</p>
<app-news></app-news>
<p>Para el footer:</p>
<app-footer></app-footer>
