import { Component, OnInit, Input } from '@angular/core';

@Component({
  selector: 'app-banner',
  templateUrl: './banner.component.html',
  styleUrls: ['./banner.component.css'],
})
export class BannerComponent implements OnInit {
  @Input() character: any = {};

  constructor() {}

  ngOnInit(): void {}
}
<meta
  name="viewport"
  content="width=device-width, initial-scale=1, maximum-scale=1"
/>
<div class="all">
  <div class="banner" style="background-color: #ffffff">
    <ngx-skeleton-loader
      class="img"
      count="1"
      appearance="line"
      animation="progress-dark"
      [theme]="{
        'height.px': 30,
        'background-color': '#FFFFFF'
      }"
    >
    </ngx-skeleton-loader>
    <div class="top" style="background-color: #ffffff; border: none">
      <img class="tulio" />
    </div>

    <div class="load" style="background: #9c4dad">
      <ngx-skeleton-loader
        count="4"
        appearance="circle"
        animation="progress-dark"
        [theme]="{
          height: '25px',
          width: '25px',
          'background-color': '#572F75'
        }"
      ></ngx-skeleton-loader>
    </div>
  </div>
</div>
